import { useState } from 'react';

import FormInput from './FormInput';

export default function SignUp() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    username: '',
    password: '',
    passwordVerification: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => {
      return { ...prevData, [name]: value };
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const {password, passwordVerification} = formData;
    if(password !== passwordVerification) {
        alert('Password verification mismatch.');
    }
    console.log(formData);
  };

  return (
    <div className='flex flex-col items-center bg-blue-200 mx-5 px-5 pb-10 mt-10'>
      <h1 className='text-3xl font-bold py-10 text-center'>Create Book Swap Account</h1>
      <form className='flex flex-col' onSubmit={handleSubmit}>
        <FormInput label='Name:' type='text' name='name' onChangeHandler={handleChange} />
        <FormInput label='E-mail:' type='email' name='email' onChangeHandler={handleChange} />
        <FormInput label='Username:' type='text' name='username' onChangeHandler={handleChange} />
        <FormInput label='Password:' type='password' name='password' onChangeHandler={handleChange} />
        <FormInput label='Confirm Password:' type='password' name='passwordConfirmation' onChangeHandler={handleChange} />
        <button className='btn btn-primary w-28 self-center' type="submit">Submit</button>
      </form>
    </div>
  );
}
