import { useState } from 'react';

import FormInput from './FormInput';

export default function SignUp() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    username: '',
    password: '',
    passwordVerification: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => {
      return { ...prevData, [name]: value };
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const {password, passwordVerification} = formData;
    if(password !== passwordVerification) {
        alert('Password verification mismatch.');
    }
    console.log(formData);
  };

  return (
    <div className='flex flex-col items-center bg-blue-200 mx-5'>
      <h1>Create Book Swap Account</h1>
      <form onSubmit={handleSubmit}>

        <FormInput label='Name:' type='text' onChangeHandler={handleChange} />
        <FormInput label='E-mail:' type='email' onChangeHandler={handleChange} />
        <FormInput label='Username:' type='text' onChangeHandler={handleChange} />
        <FormInput label='Password:' type='password' onChangeHandler={handleChange} />
        <FormInput label='Confirm Password:' type='password' onChangeHandler={handleChange} />


        <div>
          <label>Name:</label>
          <input type="text" name="name" onChange={handleChange} required />
        </div>
        <div>
          <label>E-mail:</label>
          <input type="email" name="email" onChange={handleChange} required />
        </div>
        <div>
          <label>Username:</label>
          <input type="text" name="username" onChange={handleChange} required />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" name="password" onChange={handleChange} required />
        </div>
        <div>
          <label>Re-type Password:</label>
          <input
            type="password"
            name="passwordVerification"
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
